CREATE OR REPLACE FUNCTION buscar_ref_dt_inicio_solucao (nr_prescricao_p prescr_medica.nr_prescricao%type, nr_seq_solucao_p prescr_solucao_evento.nr_seq_solucao%type) RETURNS timestamp AS $body$
DECLARE


dt_ref_inicio_solucao_w        timestamp;


BEGIN

select  min(dt_inicio)
into STRICT    dt_ref_inicio_solucao_w
from (   SELECT  max(a.dt_horario) dt_inicio
        from    prescr_solucao_evento a
        where   a.nr_prescricao = nr_prescricao_p
        and     a.ie_alteracao in (1, 2)
        and     coalesce(a.ie_evento_valido, 'S') = 'S'
        and     a.nr_seq_solucao = nr_seq_solucao_p

union all
 
        SELECT  min(dt_inicio_prescr) dt_inicio
        from    prescr_medica
        where   nr_prescricao = nr_prescricao_p) alias5;

return dt_ref_inicio_solucao_w;

end;
$body$
