SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION busca_mens_medic_prescr ( nr_prescricao_p bigint) RETURNS varchar AS $body$
DECLARE

ds_mensagem_w	varchar(4000);
ds_material_w	varchar(255);
ds_retorno_w	varchar(4000);

c01 CURSOR FOR
SELECT 	distinct b.ds_material,
		b.ds_mensagem
from		prescr_material	a,
		material		b
where	a.cd_material = b.cd_material
and		a.ie_agrupador = 1
and		a.nr_prescricao = nr_prescricao_p;


BEGIN

open c01;
loop
fetch c01 into	ds_material_w,
			ds_mensagem_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (ds_mensagem_w IS NOT NULL AND ds_mensagem_w::text <> '') then
		if (ds_retorno_w IS NOT NULL AND ds_retorno_w::text <> '') then
			ds_retorno_w := substr(ds_retorno_w||chr(10)||chr(10)||ds_material_w||': '||ds_mensagem_w,1,4000);
		else
			ds_retorno_w := substr(ds_material_w||': '||ds_mensagem_w,1,4000);
		end if;
	end if;
	end;
end loop;
close c01;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
-- REVOKE ALL ON FUNCTION busca_mens_medic_prescr ( nr_prescricao_p bigint) FROM PUBLIC;

