CREATE OR REPLACE FUNCTION calcular_altura_chumlea1985 (cd_sexo_p text, idadeAnos_p bigint, AlturaJoelhoCM bigint) RETURNS bigint AS $body$
DECLARE

/*Constantes definidas atráves de referências bibliográficas Chumlea - 1985*/

  Constante1M double precision := 64.19;
  Constante2M double precision :=  0.04;
  Constante3M double precision :=  2.02;

  Constante1F double precision := 84.88;
  Constante2F double precision :=  0.24;
  Constante3F double precision :=  1.83;

  Constante1_w double precision;
  Constante2_w double precision;
  Constante3_w double precision;

  qt_retorno_w     double precision := 0;


BEGIN

If ((position('M' in cd_sexo_p) > 0) or (position('F' in cd_sexo_p) > 0)) then

  if (position('M' in cd_sexo_p) > 0) then

    Constante1_w := Constante1M;
    Constante2_w := Constante2M;
    Constante3_w := Constante3M;

  elsif (position('F' in cd_sexo_p) > 0) then

    Constante1_w := Constante1F;
    Constante2_w := Constante2F;
    Constante3_w := Constante3F;

  end if;

  qt_retorno_w := Constante1_w - (Constante2_w * idadeAnos_p) + (Constante3_w * AlturaJoelhoCM);

end if;

return qt_retorno_w;

end;
$body$
