CREATE OR REPLACE FUNCTION calcular_dose_terap_md ( ie_unidade_vel_p text, qt_vel_infusao_p bigint, qt_concentracao_p bigint, ie_peso_p text, qt_peso_p bigint, ie_tempo_p text, ie_param_ctrl_p bigint, qt_dose_p bigint, qt_dose_total_fixo_p bigint ) RETURNS bigint AS $body$
DECLARE

qt_dose_terap_w      bigint;
qt_vel_infusao_mlh_w bigint;

BEGIN
    qt_vel_infusao_mlh_w := converte_velocidade_infusao_md(ie_unidade_vel_p, 'mlh', qt_vel_infusao_p);

    if (ie_param_ctrl_p = 0) then	
        qt_dose_terap_w := coalesce(qt_concentracao_p, 1) * coalesce(qt_vel_infusao_mlh_w, 1);
    elsif (ie_param_ctrl_p = 1) then
        qt_dose_terap_w := dividir_sem_round_md((qt_dose_p * qt_vel_infusao_mlh_w), coalesce(qt_dose_total_fixo_p, 1));
    end if;

    if (ie_peso_p = 'KG') then
      qt_dose_terap_w := dividir_sem_round_md(qt_dose_terap_w,qt_peso_p);
    end if;

    if (ie_tempo_p = 'M') then
      qt_dose_terap_w	:= dividir_sem_round((qt_dose_terap_w)::numeric,60);
    end if;

    return qt_dose_terap_w;
end;
$body$
