CREATE OR REPLACE FUNCTION calcular_horas_validas_md ( dt_prim_hor_medic_p timestamp, dt_prim_hor_prescr_p timestamp, nr_horas_validade_p bigint ) RETURNS bigint AS $body$
BEGIN

  if (dt_prim_hor_medic_p <> dt_prim_hor_prescr_p) then
	
    return coalesce(nr_horas_validade_p, 0) - coalesce(obter_hora_entre_datas_md(dt_prim_hor_prescr_p, dt_prim_hor_medic_p), 0);

