SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION calcular_irpf (vl_parametro_p bigint) RETURNS bigint AS $body$
DECLARE


vl_base_w		double precision;
tx_aliquota_w		double precision;
vl_reducao_w		double precision;

vl_irpf_w		double precision;

CRegra CURSOR FOR
	SELECT vl_base_calculo,
		 tx_aliquota,
		 vl_reducao
	from regra_calculo_irpf
	where vl_base_calculo >= vl_parametro_p
	order by vl_base_calculo desc;


BEGIN

vl_irpf_w	:= vl_parametro_p;

open CRegra;
loop
	fetch CRegra into vl_base_w,
				tx_aliquota_w,
				vl_reducao_w;
	EXIT WHEN NOT FOUND; /* apply on CRegra */

	vl_irpf_w := ((vl_parametro_p / 100) * tx_aliquota_w) - vl_reducao_w;

end loop;
close CRegra;

return coalesce(vl_irpf_w,0);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
