SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION calcular_mes_pat_bem ( dt_referencia_p timestamp, cd_estabelecimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_result_w		varchar(1) := 'N';
ie_consiste_estab_w	bigint;
dt_mes_w		timestamp;

BEGIN
if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (dt_referencia_p IS NOT NULL AND dt_referencia_p::text <> '') then
	begin
	select	count(*)
	into STRICT	ie_consiste_estab_w
	from	pat_bem b,
		pat_valor_bem a
	where	a.nr_seq_bem		= b.nr_sequencia
	and	b.cd_estabelecimento	= cd_estabelecimento_p;

	if (ie_consiste_estab_w IS NOT NULL AND ie_consiste_estab_w::text <> '') then
		begin
		select	coalesce(max(a.dt_mes_ref), trunc(clock_timestamp(), 'mm'))
		into STRICT	dt_mes_w
		from	pat_mes_ref a
		where	a.ie_situacao		=   '1'
		and	a.cd_estabelecimento	=   cd_estabelecimento_p;

		/*/--ie_result_w := dt_inicio_uso_p <= ie_mes_w;*/

		if (dt_referencia_p >= dt_mes_w) then
			begin
			ie_result_w := 'S';
			end;
		else
			begin
			ie_result_w := 'N';	
			end;
		end if;
		end;
	end if;
	end;
end if;	
return ie_result_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
