CREATE OR REPLACE FUNCTION calcular_peso_ideal_paciente (nr_seq_paciente_p paciente_atendimento.nr_seq_paciente%type, nr_seq_atendimento_p paciente_atendimento.nr_seq_atendimento%type, qt_fator_correcao_p bigint, ie_opcao_calculo_p text) RETURNS double precision AS $body$
DECLARE

					
qt_peso_ideal_w		double precision := 0.0;
nr_seq_paciente_w	bigint;
qt_peso_w			double precision;
cd_pessoa_fisica_w  varchar(10);
qt_altura_w     	bigint;


BEGIN
	nr_seq_paciente_w := nr_seq_paciente_p;

	if (coalesce(nr_seq_paciente_w::text, '') = '' and (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '')) then
		select 	max(nr_seq_paciente)
		into STRICT 	nr_seq_paciente_w
		from 	paciente_atendimento
		where 	nr_seq_atendimento = nr_seq_atendimento_p;
	end if;

	select 	max(cd_pessoa_fisica)
	into STRICT	cd_pessoa_fisica_w
	from 	paciente_setor
	where 	nr_seq_paciente = nr_seq_paciente_w;

	if (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') then
		select 	obter_peso_ciclo(nr_seq_atendimento_p),
			max(qt_altura)
		into STRICT 	qt_peso_w,
			qt_altura_w
		from 	paciente_atendimento
		where 	nr_seq_atendimento = nr_seq_atendimento_p;

	else
		select 	obter_peso_protocolo(nr_seq_paciente_w),
			max(qt_altura)
		into STRICT 	qt_peso_w,
			qt_altura_w
		from 	paciente_setor
		where 	nr_seq_paciente = nr_seq_paciente_w;
	end if;

	if (lower(ie_opcao_calculo_p) = 'peso_ideal') then
		select 	obter_peso_ideal_pac(cd_pessoa_fisica_w,
			qt_altura_w)
		into STRICT 	qt_peso_ideal_w
		;

	else
		select 	obter_peso_ideal_ajust_pac(cd_pessoa_fisica_w,
			qt_peso_w,
			qt_fator_correcao_p,
			qt_altura_w)
		into STRICT 	qt_peso_ideal_w
		;
	end if;

return qt_peso_ideal_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
