CREATE OR REPLACE FUNCTION calcular_ponto_dor_md ( ie_pior_dor_p bigint, ie_melhor_dor_p bigint, ie_dor_usual_p bigint, ie_duracao_dor_p bigint, ie_deficiencia_dor_p bigint ) RETURNS bigint AS $body$
BEGIN
  return coalesce(ie_pior_dor_p, 0) +
         coalesce(ie_melhor_dor_p, 0) +
		 dividir_md(coalesce(2 * ie_dor_usual_p, 0), 4) +
         coalesce(ie_duracao_dor_p, 0) +
		 coalesce(ie_deficiencia_dor_p, 0);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
