CREATE OR REPLACE FUNCTION calcular_ponto_incapacidade_md ( ie_trabalho_p bigint, ie_ativ_vida_diaria_p bigint, ie_dirigir_automovel_p bigint, ie_ler_p bigint, ie_televisao_p bigint, ie_ativ_fora_casa_p bigint ) RETURNS bigint AS $body$
