CREATE OR REPLACE FUNCTION calcular_score_escala_npass_md ( ie_choro_irritabilidade_p bigint, ie_comportamento_p bigint, ie_expressao_facial_p bigint, ie_tonus_extremidade_p bigint, ie_sinais_vitais_p bigint, ie_idade_corrigida_p text ) RETURNS bigint AS $body$
DECLARE

  qt_score_w smallint := 0;

BEGIN
  qt_score_w := coalesce(ie_choro_irritabilidade_p, 0) +
                coalesce(ie_comportamento_p, 0) +
				coalesce(ie_expressao_facial_p, 0) +
				coalesce(ie_tonus_extremidade_p, 0) +
                coalesce(ie_sinais_vitais_p, 0);

  if (ie_idade_corrigida_p = 'S') THEN
     qt_score_w := qt_score_w + 1;
  end if;

  return qt_score_w;
end;
