CREATE OR REPLACE FUNCTION calcular_soma_total_md ( nr_casas_diluicao_p bigint, qt_dose_dil_p bigint, qt_dose_mat_str_p bigint ) RETURNS bigint AS $body$
BEGIN

	if (nr_casas_diluicao_p > 0) then
	  return round(coalesce(qt_dose_dil_p, 0) + coalesce(qt_dose_mat_str_p, 0), coalesce(nr_casas_diluicao_p, 0));
	else
	  return(coalesce(qt_dose_dil_p, 0) + coalesce(qt_dose_mat_str_p, 0));
	end if;

end;
$body$
