CREATE OR REPLACE FUNCTION calcular_tmb_hb ( ie_sexo_p text, qt_peso_p integer, qt_altura_cm_p integer, nr_idade_p integer) RETURNS bigint AS $body$
DECLARE


qt_resultado_w	double precision:= 0;


BEGIN
begin
if (ie_sexo_p = 'M') then
	qt_resultado_w := 66.5 + (13.75 * qt_peso_p) + (5.003 * qt_altura_cm_p) - (6.775 * nr_idade_p);
elsif (ie_sexo_p = 'F') then
	qt_resultado_w := 655.1 + (9.563 * qt_peso_p) + (1.850 * qt_altura_cm_p) - (4.676 * nr_idade_p);
end if;

exception when others then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(719304);
end;

return	qt_resultado_w;

end;
$body$
