CREATE OR REPLACE FUNCTION calcular_valor_referencia_dil ( cd_material_p bigint, qt_concentracao_ideal_p bigint, cd_unid_med_concetracao_p text, cd_unid_med_base_conc_p text) RETURNS bigint AS $body$
DECLARE


qt_referencia_w		double precision;
qt_concentracao_total_w	double precision;


BEGIN

select	max(qt_concentracao_total)
into STRICT	qt_concentracao_total_w
from	material
where	cd_material	= cd_material_p;

qt_referencia_w	:= dividir(qt_concentracao_total_w, qt_concentracao_ideal_p);

return	qt_referencia_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
