CREATE OR REPLACE FUNCTION calcular_vol_total_solucao ( Tipo_dosagem_p text, qt_tempo_aplicacao_p bigint, qt_solucao_total_p bigint, qt_dosagem_p bigint, nr_prescricao_p bigint, nr_seq_solucao_p bigint, Campo_p bigint ) RETURNS bigint AS $body$
DECLARE




Gotas_W		double precision;
Tempo_W 	double precision;
Resultado_W	double precision;


BEGIN

if (upper(Tipo_dosagem_p) = 'GTM') then
	begin
	Gotas_w	:= 20;
	Tempo_w	:= 60;
	end;

elsif (upper(Tipo_dosagem_p) = 'MLH') then
	begin
	Gotas_w	:= 1;
	Tempo_w	:= 1;
	end;

else
	begin
	Gotas_w	:= 50;
	Tempo_w	:= 60;
	end;
end if;

Resultado_w	:= 0;

if (campo_p = 1) then
    Resultado_w                               := Dividir(Dividir((Round(qt_solucao_total_p) * Gotas_w) ,
	                                             qt_tempo_aplicacao_p) , Tempo_w);
	if (Resultado_w > 1) then
		Resultado_w	:= Resultado_w;
	end if;

elsif (campo_p = 2) then
    begin
    Resultado_w                               := Round(Dividir((qt_dosagem_p * tempo_w * qt_tempo_aplicacao_p), Gotas_w));

    Resultado_w                               := (Round(Resultado_w / 100) * 100);

    end;

elsif (campo_p = 3) then
    Resultado_w                               := Round(Dividir(Dividir((qt_solucao_total_p * Gotas_w), qt_dosagem_p), Tempo_w ));

end if;

if (resultado_w = 0) then
	resultado_w := null;
end if;

return resultado_w;

END;
