CREATE OR REPLACE FUNCTION calcular_vol_tot_solucao_md ( ie_ref_calculo_p text, nr_etapas_p bigint, cd_intervalo_p text, qt_volume_p bigint, qt_ocorrencia_p bigint ) RETURNS bigint AS $body$
BEGIN
  if (ie_ref_calculo_p = 'NE' and (nr_etapas_p IS NOT NULL AND nr_etapas_p::text <> '')) then
	return coalesce(qt_volume_p, 0) * coalesce(nr_etapas_p, 0);
  elsif (ie_ref_calculo_p = 'I' and (cd_intervalo_p IS NOT NULL AND cd_intervalo_p::text <> '')) then
	return coalesce(qt_volume_p, 0) * coalesce(qt_ocorrencia_p, 0);
  elsif (ie_ref_calculo_p in ('VI', 'VT')) then
	return coalesce(qt_volume_p, 0);
  else
    return 0;
  end if;
end;
