SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION calculate_patient_days ( month_p bigint, year_p bigint) RETURNS bigint AS $body$
DECLARE


patient_days_w		bigint := 0;


BEGIN

if (month_p IS NOT NULL AND month_p::text <> '' AND year_p IS NOT NULL AND year_p::text <> '') then
select
coalesce(sum(pkg_date_utils.get_diffdate(dt_entrada_unidade,dt_saida_unidade,'DAY'))+ 1 , 0)
into STRICT patient_days_w
from atend_paciente_unidade
where dt_entrada_unidade between PKG_DATE_UTILS.get_Date(year_p,month_p,1) and  fim_dia(last_day(PKG_DATE_UTILS.get_Date(year_p,month_p,1)));

end if;

return patient_days_w;



end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
