SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION calcula_digito_petrobras (cd_codigo_p text) RETURNS bigint AS $body$
DECLARE


ds_digito_w	double precision;
nr_posicao_w	bigint;
vl_soma_w	double precision := 0;
nr_inicio_calc_w	bigint := 8;
i	integer;

BEGIN
i := 0;
for i in 1..6 loop
	begin
	nr_posicao_w := substr(cd_codigo_p,i,1);
	nr_inicio_calc_w := nr_inicio_calc_w - 1;
	vl_soma_w := vl_soma_w + (nr_posicao_w * nr_inicio_calc_w);
	end;
end loop;

vl_soma_w := 11 - mod(vl_soma_w, 11);

if (vl_soma_w < 10) then
        ds_digito_w := vl_soma_w;
elsif (vl_soma_w >= 10) then
        ds_digito_w := 0;
end if;

return ds_digito_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
