CREATE OR REPLACE FUNCTION calcula_dose_total_quimio ( qt_dose_prescr_p bigint, qt_dias_util_p bigint, cd_intervalo_p text) RETURNS bigint AS $body$
DECLARE


nr_result_w	double precision;
qt_operacao_w	double precision;
IE_OPERACAO_w	varchar(10);


BEGIN
if (qt_dose_prescr_p IS NOT NULL AND qt_dose_prescr_p::text <> '') then
	begin
	select 	coalesce(max(a.qt_operacao),1),
		max(IE_OPERACAO)
	into STRICT	qt_operacao_w,
		IE_OPERACAO_w
	from	intervalo_prescricao a
	where  	a.cd_intervalo = cd_intervalo_p;

	if (IE_OPERACAO_w = 'H') and (qt_operacao_w	> 0)then

		qt_operacao_w	:= Ceil(24 / qt_operacao_w);
		nr_result_w	:= 	(qt_dose_prescr_p * coalesce(qt_dias_util_p,1)) * qt_operacao_w;
	else
		nr_result_w	:= 	(qt_dose_prescr_p * coalesce(qt_dias_util_p,1)) * qt_operacao_w;
	end if;
	end;
end if;

return	nr_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
