CREATE OR REPLACE FUNCTION calcula_escala_morse_md (IE_HIST_QUEDA_P text, IE_DIAG_SECUNDARIO_P text, IE_USO_HEPARINA_P text, IE_AJUDA_DEAMBULAR_P bigint, IE_MODO_ANDAR_P bigint, IE_ESTADO_MENTAL_P bigint ) RETURNS bigint AS $body$
DECLARE


   qt_pontuacao_w	bigint := 0;

BEGIN
    --INCIO MD
    If (IE_HIST_QUEDA_P = 'S') then
      qt_pontuacao_w := qt_pontuacao_w + 25;
    end if;

    If (IE_DIAG_SECUNDARIO_P = 'S') then
      qt_pontuacao_w := qt_pontuacao_w + 15;
    end if;

    If (IE_USO_HEPARINA_P = 'S') then
      qt_pontuacao_w := qt_pontuacao_w + 20;
    end if;

    qt_pontuacao_w := qt_pontuacao_w + coalesce(IE_AJUDA_DEAMBULAR_P,0);

    qt_pontuacao_w := qt_pontuacao_w + coalesce(IE_MODO_ANDAR_P,0);

    qt_pontuacao_w := qt_pontuacao_w + coalesce(IE_ESTADO_MENTAL_P,0);
    --FIM MD
    RETURN coalesce(qt_pontuacao_w,0);
   
END;
