CREATE OR REPLACE FUNCTION calcula_escala_tenetti_md ( ie_equilibrio_sentado_p bigint, ie_levantar_cadeira_p bigint, ie_equilibrio_imediato_p bigint, ie_equilibrio_em_pe_p bigint, ie_equilibrio_olho_fechado_p bigint, ie_equilibrio_girar_p bigint, ie_empurrao_torax_p bigint, ie_girar_pescoco_p bigint, ie_equilibrio_apoio_p bigint, ie_extensao_tronco_p bigint, ie_alcancar_p bigint, ie_sentar_p bigint ) RETURNS bigint AS $body$
