CREATE OR REPLACE FUNCTION calcula_horario_etiqueta (ds_horario_inicial_p bigint, ie_intervalo_p bigint, ie_opcao_p text, ds_horario_final_p bigint default null) RETURNS varchar AS $body$
DECLARE



ds_retorno_w  varchar(255);
ds_horario_w bigint;
ie_contador_w bigint := 0;


BEGIN

ds_horario_w := ds_horario_inicial_p;
ds_retorno_w := to_char(ds_horario_w);

while ((((ds_horario_w+ie_intervalo_p) < ds_horario_final_p) and (ds_horario_final_p IS NOT NULL AND ds_horario_final_p::text <> ''))
	or (((ds_horario_w+ie_intervalo_p) < 24) and (coalesce(ds_horario_final_p::text, '') = '')))

	loop
	begin

	if (mod(length(ds_retorno_w),2) <> 0) then
		ds_retorno_w := substr(ds_retorno_w,1,length(ds_retorno_w)-1)||'0'||substr(ds_retorno_w,length(ds_retorno_w),length(ds_retorno_w));
	end if;

	ds_retorno_w := ds_retorno_w || to_char(ds_horario_w + ie_intervalo_p);
	ds_horario_w := ds_horario_w + ie_intervalo_p;
	ie_contador_w := ie_contador_w + 1;

	end;
	end loop;

if (ie_opcao_p = 'C') then
	ds_retorno_w := to_char(ie_contador_w+1);
end if;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
