CREATE OR REPLACE FUNCTION calcula_idade_md (qt_idade_p bigint) RETURNS bigint AS $body$
DECLARE

  qt_pontuacao_w smallint;

BEGIN
  qt_pontuacao_w := 0;

  if (qt_idade_p >= 80) then
    qt_pontuacao_w := 17;
  elsif (qt_idade_p >= 70) then
    qt_pontuacao_w := 13;
