CREATE OR REPLACE FUNCTION calcula_pos_topo_entidade (qt_entidade_atual_p bigint,qt_entidade_p bigint,qt_pos_topo_atual_p bigint) RETURNS bigint AS $body$
DECLARE

qt_pos_topo_w bigint;

BEGIN

	qt_pos_topo_w := qt_pos_topo_atual_p;

	if ( coalesce(qt_pos_topo_w::text, '') = '' ) then
		qt_pos_topo_w := 10;
	end if;

	if ( qt_entidade_atual_p >= qt_entidade_p ) then
		qt_pos_topo_w := qt_pos_topo_w + 100;
	end if;
	return qt_pos_topo_w;
end;
