CREATE OR REPLACE FUNCTION calc_espacamento_campo_relat ( nr_sequencia_p bigint, ie_opcao_p text) RETURNS bigint AS $body$
DECLARE


nr_sequencia_w		bigint;
nr_seq_apresentacao_w	integer;
nr_seq_banda_w		bigint;

qt_esquerda_w		real;
qt_tamanho_w		real;

qt_total_w		bigint;

c01 CURSOR FOR
	SELECT		qt_esquerda,
			qt_tamanho
	from		banda_relat_campo
	where		nr_seq_banda = nr_seq_banda_w
	and		nr_seq_apresentacao <= nr_seq_apresentacao_w
	order by	nr_seq_apresentacao;


BEGIN

qt_total_w := 0;

select	nr_seq_apresentacao,
	nr_seq_banda,
	qt_tamanho,
	nr_sequencia
into STRICT	nr_seq_apresentacao_w,
	nr_seq_banda_w,
	qt_tamanho_w,
	nr_sequencia_w
from	banda_relat_campo
where	nr_sequencia = nr_sequencia_p;

open C01;
loop
fetch C01 into
	qt_esquerda_w,
	qt_tamanho_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
begin
	if (ie_opcao_p = 'PPE') then -- Próxima posição esquerda
		qt_total_w := (qt_esquerda_w + qt_tamanho_w) + 5;
	end	if;
end;
end loop;
close C01;

return	qt_total_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
