SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION calc_expiry_no_of_days (cd_medico_p text,cd_pessoa_fisica_p text) RETURNS bigint AS $body$
DECLARE

  noofdays_w bigint;

BEGIN
  if (cd_medico_p IS NOT NULL AND cd_medico_p::text <> '' AND cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
    SELECT PKG_DATE_UTILS.get_DiffDate(clock_timestamp(),
                                       p.dt_ausencia_trabalho,
                                       'DAY')
      into STRICT noofdays_w
      from atestado_paciente p
     where p.nm_usuario =wheb_usuario_pck.get_nm_usuario
       and p.cd_medico = cd_medico_p
       and p.cd_pessoa_fisica = cd_pessoa_fisica_p
       and p.ie_situacao='A' and (p.dt_liberacao IS NOT NULL AND p.dt_liberacao::text <> '')  order by dt_atualizacao desc  LIMIT 1;
    return noofdays_w;
  end if;
  return 0;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
