CREATE OR REPLACE FUNCTION calc_hor_esp_sem_dt_lib_md ( ie_se_necessario_p text, ie_acm_p text, dt_prev_execucao_pp timestamp, tipo_ret bigint ) RETURNS varchar AS $body$
DECLARE

    ie_horario_especial_w varchar(1) := 'N';

BEGIN
    IF ( tipo_ret = 1 ) THEN
        IF ( ie_se_necessario_p = 'S' ) OR ( ie_acm_p = 'S' ) THEN
