SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION calc_hor_real_per_res_migr ( nr_seq_projeto_p bigint, dt_resumo_p timestamp) RETURNS bigint AS $body$
DECLARE


qt_horas_real_os_w	double precision := 0;
dt_previsao_anterior_w	timestamp;
dt_resumo_w		timestamp;


BEGIN
if (nr_seq_projeto_p IS NOT NULL AND nr_seq_projeto_p::text <> '') and (dt_resumo_p IS NOT NULL AND dt_resumo_p::text <> '') then
	begin
	dt_resumo_w := dt_resumo_p - 1/86400;

	select	coalesce(max(dt_resumo),to_date(to_char(dt_resumo_p,'dd/mm/yyyy') || ' 08:00:00','dd/mm/yyyy hh24:mi:ss'))
	into STRICT	dt_previsao_anterior_w
	from	w_resumo_migracao
	where	nr_seq_projeto = nr_seq_projeto_p
	and	trunc(dt_resumo,'dd') = trunc(dt_resumo_p,'dd');

	select	coalesce(dividir(sum(a.qt_minuto),60),0)
	into STRICT	qt_horas_real_os_w
	from	man_ordem_serv_ativ a
	where	trunc(a.dt_atividade,'dd') = trunc(dt_resumo_p,'dd')
	and	((a.dt_atividade between dt_previsao_anterior_w and dt_resumo_w) or (to_char(dt_resumo_p,'hh24') = '09' and a.dt_atividade < dt_previsao_anterior_w) or /* tratamento colaboradores que chegam as 7:00 */
		 (to_char(dt_resumo_p,'hh24') = '18' and a.dt_atividade > dt_resumo_w)) /* tratamento atividades registradas ap√≥s as 18:00 */
	and	exists (
			SELECT	1
			from	proj_cron_etapa e,
				proj_cronograma c,
				proj_projeto p
			where	e.nr_seq_cronograma = c.nr_sequencia
			and	c.nr_seq_proj = p.nr_sequencia
			and	p.nr_sequencia = nr_seq_projeto_p
			and	e.nr_sequencia = a.nr_seq_proj_cron_etapa
			and	p.nr_seq_ordem_serv = a.nr_seq_ordem_serv);
	end;
end if;
return qt_horas_real_os_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
