SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION calc_macro_digito_verificador (ds_valor_p text) RETURNS varchar AS $body$
DECLARE


nr_checksum_w 	varchar(255);
char_aux		varchar(1);
nr_bin		numeric(37) := 0;
i		bigint := 0;



BEGIN
if (ds_valor_p IS NOT NULL AND ds_valor_p::text <> '') then
	begin
	for i in 1..length(ds_valor_p) loop
		char_aux := substr(ds_valor_p,i,1);
		nr_bin := nr_bin + ASCII(char_aux);
	end loop;

	nr_bin := (nr_bin mod 256);

	nr_checksum_w := to_base(nr_bin,16);

	end;
end if;

if (length(nr_checksum_w) <= 1) then
	nr_checksum_w := '0'||nr_checksum_w;
end if;

return nr_checksum_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
