CREATE OR REPLACE FUNCTION calc_numero_dias_internacao (NR_ATENDIMENTO_P ATENDIMENTO_PACIENTE.NR_ATENDIMENTO%TYPE, DT_AVALIACAO_P timestamp) RETURNS varchar AS $body$
DECLARE

RETORNO_W varchar(40);

BEGIN
    BEGIN
      SELECT PKG_DATE_UTILS.GET_DIFFDATE(DT_ENTRADA, DT_AVALIACAO_P, 'DAY')
      INTO STRICT   RETORNO_W
      FROM   ATENDIMENTO_PACIENTE
      WHERE  NR_ATENDIMENTO = NR_ATENDIMENTO_P;
    EXCEPTION
      WHEN no_data_found THEN
        RETORNO_W := ' ';
    END;
RETURN RETORNO_W;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
