CREATE OR REPLACE FUNCTION campos_nota_fiscal_intermed ( nr_sequencia_p bigint, ie_linha_p bigint) RETURNS varchar AS $body$
DECLARE



/* FUNCTION CRIADA ESPECIFICAMENTE PARA INTERMED
UTILIZADA NO RELATORIO MATRICIAL, ONDE DEVE IMPRIMIR
OS VENCIMENTOS EM COLUNAS E ESPAÃ‡OS FIXOS*/
vl_retorno_w			varchar(255);
vl_resultado_w		varchar(255);
ie_parcela_w			integer;
dt_vencimento_w		timestamp;
vl_vencimento_w		varchar(10);
vl_novo_vencimento_w		varchar(10);
i				integer;


C01 CURSOR FOR
SELECT	row_number() OVER () AS rownum,
	dt_vencimento,
	substr(campo_mascara_virgula(vl_vencimento),1,10)
from	nota_fiscal_venc
where	nr_sequencia = nr_sequencia_p;


BEGIN

OPEN C01;
LOOP
FETCH C01 into
	ie_parcela_w,
	dt_vencimento_w,
	vl_vencimento_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (length(vl_vencimento_w) <= 9) then
		vl_novo_vencimento_w		:= vl_vencimento_w;
		i 	:= 0;
		for	i in length(vl_vencimento_w)..9 loop
			vl_novo_vencimento_w	:= ' ' || vl_novo_vencimento_w;
		end loop;
	vl_vencimento_w	:= vl_novo_vencimento_w;
	end if;
	if	((ie_linha_p = 1) and (ie_parcela_w in (1,4,7))) or
		((ie_linha_p = 2) and (ie_parcela_w in (2,5,8))) or
		((ie_linha_p = 3) and (ie_parcela_w in (3,6,9))) then
		begin
		if (ie_parcela_w in (1,2,3)) then
			vl_resultado_w	:= vl_resultado_w || ie_parcela_w || '  ' ||
					dt_vencimento_w || ' ' || vl_vencimento_w;
		else
			vl_resultado_w	:= vl_resultado_w || '    ' || ie_parcela_w || '    ' ||
					dt_vencimento_w || '  ' || vl_vencimento_w;
		end if;
		end;
	end if;
	end;
END LOOP;
CLOSE C01;

vl_retorno_w	:= vl_resultado_w;

RETURN vl_retorno_w;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
