CREATE OR REPLACE FUNCTION campo_aspas ( ds_campo_p text, ie_posicao_final_p bigint) RETURNS varchar AS $body$
DECLARE


ds_campo_w		varchar(255);
qt_tamanho_w		smallint;
qt_espaco_w		smallint;
i			smallint;


BEGIN

select	'"' || ds_campo_p
into STRICT	ds_campo_w
;

select	length(ds_campo_w)
into STRICT	qt_tamanho_w
;

select	((ie_posicao_final_p - 1) - qt_tamanho_w)
into STRICT	qt_espaco_w
;

FOR i IN 1..qt_espaco_w LOOP
	ds_campo_w	:= ds_campo_w || ' ';
END LOOP;
ds_campo_w	:= ds_campo_w || '"';

return ds_campo_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
