CREATE OR REPLACE FUNCTION campo_mascara_cod_mat_xml_fat ( cd_material_p text, ds_mascara_p text) RETURNS varchar AS $body$
DECLARE

ds_retorno_w			varchar(30);
cd_material_w			varchar(30);
ds_mascara_w			varchar(30);


BEGIN
cd_material_w	:= trim(both to_char(cd_material_p));
ds_mascara_w	:= trim(both to_char(ds_mascara_p));

if (cd_material_w IS NOT NULL AND cd_material_w::text <> '') then
	if	((ds_mascara_w IS NOT NULL AND ds_mascara_w::text <> '') or (length(cd_material_w) > length(ds_mascara_w)))then
		ds_retorno_w	:= substr(ds_mascara_w || cd_material_w, length(cd_material_w) + 1, length(ds_mascara_w));
	else
		ds_retorno_w	:= cd_material_w;
	end if;
end if;

return	ds_retorno_w;

end;
$body$
