CREATE OR REPLACE FUNCTION campo_mascara_oftalmo (vl_campo_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w	varchar(255);


BEGIN

ds_retorno_w	:= Campo_Mascara_virgula_casas(vl_campo_p,2);

if (vl_campo_p	> 0) then
