CREATE OR REPLACE FUNCTION can_obter_und_medida_mat (cd_unidade_medida_p text, cd_material_p bigint) RETURNS varchar AS $body$
DECLARE


ds_unidade_medida_w	unidade_medida.ds_unidade_medida%type;


BEGIN
if (cd_unidade_medida_p IS NOT NULL AND cd_unidade_medida_p::text <> '') then
        begin
                select ds_unidade_medida
                into STRICT ds_unidade_medida_w
                from unidade_medida_dose_v
                where cd_material = cd_material_p
                and cd_unidade_medida = cd_unidade_medida_p
                and ie_prioridade  not in (8,9);
        exception
        when others then
                return ds_unidade_medida_w;
        end;

end if;

return	ds_unidade_medida_w;

end;
$body$
LANGUAGE PLPGSQL
