CREATE OR REPLACE FUNCTION ca_obter_desc_atividade (nr_seq_atividade_p bigint) RETURNS varchar AS $body$
DECLARE

ds_atividade_w	varchar(100);


BEGIN

if (coalesce(nr_seq_atividade_p,0) > 0) then

	select	substr(ds_atividade,1,100)
	into STRICT	ds_atividade_w
	from	ca_atividade
	where	nr_sequencia = nr_seq_atividade_p;

end if;

return	ds_atividade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
