CREATE OR REPLACE FUNCTION ca_obter_desc_tipo_atividade (nr_seq_tipo_atividade_p bigint, nr_seq_atividade_p bigint) RETURNS varchar AS $body$
DECLARE


ds_tipo_atividade_w	varchar(100);
nr_seq_tipo_atividade_w	bigint;


BEGIN

nr_seq_tipo_atividade_w := nr_seq_tipo_atividade_p;

if (coalesce(nr_seq_atividade_p,0) > 0) then

	select	coalesce(Max(nr_seq_tipo_atividade),0)
	into STRICT	nr_seq_tipo_atividade_w
	from	ca_atividade
	where	nr_sequencia = nr_seq_atividade_p;

end if;

if (coalesce(nr_seq_tipo_atividade_w,0) > 0) then

		select	substr(DS_TIPO_ATIVIDADE,1,100)
		into STRICT	ds_tipo_atividade_w
		from	CA_TIPO_ATIVIDADE
		where	nr_sequencia = nr_seq_tipo_atividade_w;

end if;

return	ds_tipo_atividade_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
