BEGIN

select 	substr(CASE WHEN b.ie_tipo_resultado='N' THEN  REPLACE(to_char(a.vl_resultado,'99999999990.9999'),' ','')  ELSE a.ds_resultado END ,1,255)
into STRICT	ds_retorno_w
