CREATE OR REPLACE FUNCTION ca_obter_status_atividade ( nr_sequencia_p bigint) RETURNS varchar AS $body$
DECLARE


/* IE_RETORNO
P = Pendente
A = Pendente em atraso
C = Cancelado
R = Realizado
*/
ie_status_w		varchar(15);
ie_retorno_w		varchar(15);
dt_prevista_w		timestamp;


BEGIN

select	ie_status,
	trunc(coalesce(dt_prevista,clock_timestamp()))
into STRICT	ie_status_w,
	dt_prevista_w
from	ca_controle_atividade
where	nr_sequencia = nr_sequencia_p;

if (ie_status_w = 'P') and (dt_prevista_w < trunc(clock_timestamp())) then
	ie_retorno_w	:= 'A';
else
	ie_retorno_w	:= ie_status_w;
end if;

return	ie_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
