CREATE OR REPLACE FUNCTION ccd_comments_removal ( TEXT_BLOB text ) RETURNS text AS $body$
DECLARE

RET_TEXT_WO_COMENT text;
VAR_SUPP_LOOP text;
VAR_INDEX bigint;

BEGIN
  RET_TEXT_WO_COMENT := '';
  VAR_SUPP_LOOP := TEXT_BLOB;
  VAR_INDEX := position('/*' in VAR_SUPP_LOOP);
  WHILE VAR_INDEX > 0 LOOP
  BEGIN
    RET_TEXT_WO_COMENT := RET_TEXT_WO_COMENT || SUBSTR(VAR_SUPP_LOOP, 0, position('/*' in VAR_SUPP_LOOP) - 1);
    VAR_SUPP_LOOP := SUBSTR(VAR_SUPP_LOOP, position('*/' in VAR_SUPP_LOOP) + 2);
    VAR_INDEX := position('/*' in VAR_SUPP_LOOP);
  END;
  END LOOP;
  RET_TEXT_WO_COMENT := RET_TEXT_WO_COMENT || VAR_SUPP_LOOP;

  VAR_SUPP_LOOP := RET_TEXT_WO_COMENT;
  RET_TEXT_WO_COMENT := '';
  VAR_INDEX := position('--' in VAR_SUPP_LOOP);
  WHILE VAR_INDEX > 0 LOOP
  BEGIN
    RET_TEXT_WO_COMENT := RET_TEXT_WO_COMENT || SUBSTR(VAR_SUPP_LOOP, 0, position('--' in VAR_SUPP_LOOP) - 1);
    VAR_SUPP_LOOP := SUBSTR(VAR_SUPP_LOOP, position('--' in VAR_SUPP_LOOP));
    IF position(CHR(10) in VAR_SUPP_LOOP) > 0 THEN
      VAR_SUPP_LOOP := SUBSTR(VAR_SUPP_LOOP, position(CHR(10) in VAR_SUPP_LOOP));
    ELSE
      VAR_SUPP_LOOP := '';
    END IF;
    VAR_INDEX := position('--' in VAR_SUPP_LOOP);
  END;
  END LOOP;
  RET_TEXT_WO_COMENT := RET_TEXT_WO_COMENT || VAR_SUPP_LOOP;

  RETURN RET_TEXT_WO_COMENT;
END;
