CREATE OR REPLACE FUNCTION ccih_obter_microorgan_concat ( nr_seq_atend_precaucao_p bigint) RETURNS varchar AS $body$
DECLARE


ds_microorganismo_w	varchar(255);
ds_microorganismo_ww	varchar(2000);

C01 CURSOR FOR
	SELECT	x.ds_microorganismo
	from	cih_microorganismo x,
		atend_precaucao_micro a
	where	a.cd_microorganismo = x.cd_microorganismo
	and	x.ie_situacao = 'A'
	and	a.nr_seq_atend_precaucao = nr_seq_atend_precaucao_p
	order by	1;

BEGIN
if (nr_seq_atend_precaucao_p IS NOT NULL AND nr_seq_atend_precaucao_p::text <> '') then

	open C01;
	loop
	fetch C01 into
		ds_microorganismo_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		ds_microorganismo_ww	:= ds_microorganismo_ww||ds_microorganismo_w||',';
		end;
	end loop;
	close C01;

	if (length(ds_microorganismo_ww) > 1) then
		ds_microorganismo_ww	:= substr(ds_microorganismo_ww, 1, length(ds_microorganismo_ww)-1);
	end if;
end if;

return	ds_microorganismo_ww;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
