CREATE OR REPLACE FUNCTION ccih_obter_se_lib_vigente ( dt_prescricao_p timestamp, qt_dias_liberado_p bigint) RETURNS varchar AS $body$
DECLARE


dt_vigencia_w	timestamp;
ds_retorno_w	varchar(1);

BEGIN

if (qt_dias_liberado_p IS NOT NULL AND qt_dias_liberado_p::text <> '') then
	select 	dt_prescricao_p + qt_dias_liberado_p
	into STRICT	dt_vigencia_w
	;

	if (clock_timestamp() between dt_prescricao_p and dt_vigencia_w) then
		ds_retorno_w := 'S';
	else
		ds_retorno_w := 'N';
	end if;
elsif (trunc(dt_prescricao_p) = trunc(clock_timestamp())) then
	ds_retorno_w := 'S';
else
	ds_retorno_w := 'N';
end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
