CREATE OR REPLACE FUNCTION ccih_obter_taxa_doente_ih (dt_inicial_p timestamp, dt_final_p timestamp, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE

 
qt_total_fichas_mes_w	bigint;
qt_total_alta_obito_w	bigint;
vl_taxa_doente_w	double precision;

BEGIN
if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') then 
	--Total de Fichas de Ocorrência Mensal 
	select	count(*) 
	into STRICT	qt_total_fichas_mes_w 
	from	cih_ficha_ocorrencia a, 
		atendimento_paciente b 
	where	a.nr_atendimento	= b.nr_atendimento 
	and	b.cd_estabelecimento	= cd_estabelecimento_p 
	and	exists (SELECT	1 
			from	cih_local_infeccao c 
			where	c.nr_ficha_ocorrencia = a.nr_ficha_ocorrencia 
			and	c.dt_infeccao between trunc(dt_inicial_p) and trunc(dt_final_p)+86399/86400);
	 
	--Total de Altas + Óbitos 
	select (sum(x.nr_altas) + sum(x.nr_obitos)) 
	into STRICT	qt_total_alta_obito_w 
	from	eis_ocupacao_setor_v x 
	where	x.ie_periodo = 'M' 
	and	dt_referencia between trunc(dt_inicial_p) and trunc(dt_final_p)+86399/86400 
	and	((x.cd_estabelecimento = cd_estabelecimento_p) or (cd_estabelecimento_p = '0'));
	 
	 
 
 
	vl_taxa_doente_w	:=	(qt_total_fichas_mes_w / qt_total_alta_obito_w)*100;
 
end if;
 
return	vl_taxa_doente_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
