CREATE OR REPLACE FUNCTION ccih_obt_calc_taxa_de_ih (dt_inicial_p timestamp, dt_final_p timestamp, cd_estabelecimento_p bigint) RETURNS bigint AS $body$
DECLARE


nr_ih_w			double precision;
nr_altas_obitos_w	bigint;
vl_taxa_ih_w		double precision;

BEGIN
if (dt_inicial_p IS NOT NULL AND dt_inicial_p::text <> '') and (dt_final_p IS NOT NULL AND dt_final_p::text <> '') then

	--Nº de Infecções Hospitalares
	select	count(*)
	into STRICT	nr_ih_w
	from	cih_caso_infeccao b,
		cih_local_infeccao a,
		cih_ficha_ocorrencia_v c
	where	a.nr_ficha_ocorrencia	= c.nr_ficha_ocorrencia
	and	a.cd_caso_infeccao	= b.cd_caso_infeccao
	and	a.cd_caso_infeccao	= 1
	and	a.dt_infeccao between trunc(dt_inicial_p) and trunc(dt_final_p)+86399/86400
	and	((c.cd_estabelecimento	= cd_estabelecimento_p) or (cd_estabelecimento_p = '0'));

	--Número de Altas + Óbitos
	select (sum(x.nr_altas) + sum(x.nr_obitos))
	into STRICT	nr_altas_obitos_w
	from	eis_ocupacao_setor_v x
	where	x.ie_periodo = 'M'
	and	dt_referencia between trunc(dt_inicial_p) and trunc(dt_final_p)+86399/86400
	and	((x.cd_estabelecimento = cd_estabelecimento_p) or (cd_estabelecimento_p = '0'));

	vl_taxa_ih_w	:= (nr_ih_w / nr_altas_obitos_w)*100;
end if;

return	vl_taxa_ih_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
