CREATE OR REPLACE FUNCTION ceon_obter_dias_ciclo_virgula ( nr_seq_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ds_retorno_w		varchar(2000) := '';
ds_ciclo_w		varchar(255);
nr_seq_paciente_w		bigint;
nr_seq_protocolo_w		bigint;
C01 CURSOR FOR
	SELECT 	DISTINCT ds_dia_ciclo
	FROM 	paciente_atendimento a
	WHERE a.nr_seq_paciente = nr_seq_paciente_w
	AND 	a.nr_seq_protocolo = nr_seq_protocolo_w
	AND 	coalesce(a.dt_cancelamento::text, '') = ''
	ORDER BY (SUBSTR(a.ds_dia_ciclo,2,4))::numeric;


BEGIN

select 	max(a.nr_seq_paciente),
		max(a.nr_seq_protocolo)
into STRICT	nr_seq_paciente_w,
		nr_seq_protocolo_w
from	paciente_atendimento a
where 	a.nr_seq_atendimento = nr_seq_atendimento_p;



OPEN C01;
LOOP
FETCH C01 INTO
	ds_ciclo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	BEGIN
	ds_retorno_w 		:= ds_retorno_w ||ds_ciclo_w||',';
	END;
END LOOP;
CLOSE C01;

RETURN	ds_retorno_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
