CREATE OR REPLACE FUNCTION ceon_obter_se_ciclo_dia ( nr_seq_atendimento_p bigint, cd_material_p bigint, nr_banda_relat_p bigint default 1) RETURNS varchar AS $body$
DECLARE


ds_retorno_w			varchar(2000) := '';
ds_retorno_ww			varchar(2000) := '';
ds_dias_aplicacao_w		varchar(4000);
ds_dias_aplicacao_ww	varchar(4000);
ds_dias_aplicacao_c01_w	varchar(4000);
cont_w			bigint := 0;
posicao_w		smallint;
posicao_ww		smallint;
dia_w			varchar(50);
dia_ww			varchar(50);
dia_ciclo_w		varchar(255);
ds_ciclo_w		varchar(255);
nr_seq_paciente_w bigint;
nr_ciclo_w		bigint;

C01 CURSOR FOR
	SELECT a.ds_dia_ciclo
	from   paciente_atendimento a,
		   paciente_atend_medic b
	where  a.nr_seq_atendimento = b.nr_seq_atendimento
	and    b.cd_material = cd_material_p
	and    a.nr_seq_paciente = nr_seq_paciente_w
	and 	a.nr_ciclo = nr_ciclo_w;


BEGIN
select  max(nr_seq_paciente),
		max(nr_ciclo)
into STRICT	nr_seq_paciente_w,
		nr_ciclo_w
from 	paciente_atendimento
where 	nr_seq_atendimento = nr_seq_atendimento_p;

ds_dias_aplicacao_w := ceon_Obter_dias_ciclo_virgula(nr_seq_atendimento_p);

if (nr_banda_relat_p > 1) then
	ds_dias_aplicacao_w := remove_dias_aplicacao(ds_dias_aplicacao_w, (nr_banda_relat_p - 1) * 7);
end if;
	while	(ds_dias_aplicacao_w IS NOT NULL AND ds_dias_aplicacao_w::text <> '') loop
		begin
		posicao_ww				:= position(',' in ds_dias_aplicacao_w);
		dia_ww					:= substr(ds_dias_aplicacao_w,1,posicao_ww - 1);
		ds_dias_aplicacao_w	:= substr(ds_dias_aplicacao_w,posicao_ww + 1,length(ds_dias_aplicacao_w));
		ds_retorno_ww 			:= '                   ';

		open C01;
		loop
		fetch C01 into
			ds_dias_aplicacao_c01_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin

			if (ds_dias_aplicacao_c01_w = dia_ww) then
				ds_retorno_ww 			:= '        X         ';
			end if;

			end;
		end loop;
		close C01;
		cont_w := cont_w + 1;
		ds_retorno_w := ds_retorno_w||ds_retorno_ww;
		if (cont_w >= 7) then
			exit;
		end if;

		end;
	end loop;

return ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
