CREATE OR REPLACE FUNCTION cettro_obter_aplic_item_prescr ( nr_prescricao_p bigint, nr_seq_item_p bigint, qt_etiqueta_p bigint) RETURNS varchar AS $body$
DECLARE



qt_hr_aplicacao_w	smallint;
qt_mi_aplicacao_w	smallint;
qt_solucao_w		double precision;
ds_aplicacao_w		varchar(255);
vl_aux_w		integer;
vl_hora_w		varchar(2);
qt_minutos_w		double precision;
ds_hora_w		varchar(10);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then

	select	max(qt_hora_aplicacao),
		max(qt_min_aplicacao),
		max(qt_solucao)
	into STRICT	qt_hr_aplicacao_w,
		qt_mi_aplicacao_w,
		qt_solucao_w
	from	prescr_material
	where	nr_prescricao	= nr_prescricao_p
	and	nr_sequencia	= nr_seq_item_p;

	if (coalesce(qt_etiqueta_p,1) = 1) then

		if (qt_hr_aplicacao_w IS NOT NULL AND qt_hr_aplicacao_w::text <> '') then
			ds_aplicacao_w := to_char(qt_hr_aplicacao_w) || 'h';
		end if;

		if (qt_mi_aplicacao_w IS NOT NULL AND qt_mi_aplicacao_w::text <> '') then
			if (ds_aplicacao_w IS NOT NULL AND ds_aplicacao_w::text <> '') then
				ds_aplicacao_w := ds_aplicacao_w || ' ' || to_char(qt_mi_aplicacao_w) || 'min';
			else
				ds_aplicacao_w := to_char(qt_mi_aplicacao_w) || 'min';
			end if;
		end if;

	else
		qt_minutos_w	:= (dividir(coalesce(qt_hr_aplicacao_w,0),coalesce(qt_etiqueta_p,1)) * 60) + dividir(coalesce(qt_mi_aplicacao_w,0),coalesce(qt_etiqueta_p,1));

		if (qt_minutos_w > 0) then

			vl_aux_w	:= (qt_minutos_w - (trunc(qt_minutos_w / 60) * 60));

			if (vl_aux_w < 10) then
				vl_hora_w := '0' || to_char(vl_aux_w);
			else
				vl_hora_w := substr(to_char(vl_aux_w),1,2);
			end if;

			if (trunc(qt_minutos_w / 60) > 0) then
				ds_hora_w := trunc(qt_minutos_w / 60) || ' h ';
			end if;

			ds_aplicacao_w	:=  ds_hora_w || vl_hora_w || ' min';

		end if;

	end if;

	if (qt_solucao_w IS NOT NULL AND qt_solucao_w::text <> '') then
		if (ds_aplicacao_w IS NOT NULL AND ds_aplicacao_w::text <> '') then
			ds_aplicacao_w := ds_aplicacao_w || ' ' || to_char(qt_solucao_w) || obter_unid_med_usua('ml');
		else
			ds_aplicacao_w := to_char(qt_solucao_w) || obter_unid_med_usua('ml');
		end if;
	end if;

	if (ds_aplicacao_w IS NOT NULL AND ds_aplicacao_w::text <> '') then
		ds_aplicacao_w := '(' || ds_aplicacao_w || ')';
	end if;

end if;

return ds_aplicacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
