CREATE OR REPLACE FUNCTION cf_obter_classif_pend_lib ( nr_seq_classificacao_p bigint, nr_interno_conta_p bigint) RETURNS varchar AS $body$
DECLARE

/* +++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ 
Finalidade: 
------------------------------------------------------------------------------------------------------------------- 
Locais de chamada direta: 
[ ] Objetos do dicionário [ ] Tasy (Delphi/Java) [ ] Portal [ ] Relatórios [ ] Outros: 
 ------------------------------------------------------------------------------------------------------------------ 
Pontos de atenção: 
++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++++ */
 
ds_retorno_w			varchar(1);
nr_seq_status_fat_w		bigint;
nr_seq_regra_fluxo_w	bigint;
ie_pendencia_regra_w	varchar(3);
ie_pendencia_cadastro_w	varchar(3);
ie_pendencia_w			varchar(3);
qt_existe_regra_w		bigint;


BEGIN 
 
ds_retorno_w:= 'N';
 
select 	coalesce(max(nr_seq_status_fat),0), 
		coalesce(max(nr_seq_regra_fluxo),0) 
into STRICT	nr_seq_status_fat_w, 
		nr_seq_regra_fluxo_w 
from 	conta_paciente 
where 	nr_interno_conta = nr_interno_conta_p;
 
 
if (nr_seq_status_fat_w > 0) then 
 
		select 	max(ie_pendencia) 
		into STRICT	ie_pendencia_regra_w 
		from 	cf_regra_estagio 
		where 	nr_seq_regra = nr_seq_regra_fluxo_w 
		and 	nr_seq_status_fat = nr_seq_status_fat_w;
		 
		select 	max(ie_pendencia) 
		into STRICT	ie_pendencia_cadastro_w 
		from 	cf_status_faturamento 
		where 	nr_sequencia = nr_seq_status_fat_w;
		 
		ie_pendencia_w:= null;
		 
		if (ie_pendencia_regra_w IS NOT NULL AND ie_pendencia_regra_w::text <> '') then 
				ie_pendencia_w:= ie_pendencia_regra_w;
		elsif (ie_pendencia_cadastro_w IS NOT NULL AND ie_pendencia_cadastro_w::text <> '') then 
				ie_pendencia_w:= ie_pendencia_cadastro_w;
		end if;
 
		select 	count(*) 
		into STRICT	qt_existe_regra_w 
		from 	cf_liberacao_pendencia 
		where 	ie_pendencia = ie_pendencia_w 
		and 	ie_situacao = 'A' 
		and 	nr_seq_classif_pend = nr_seq_classificacao_p;
		 
		if (qt_existe_regra_w > 0) then 
				ds_retorno_w:= 'S';
		end if;
		 
end if;
 
return	ds_retorno_w;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
