CREATE OR REPLACE FUNCTION checagem_sala_palm (nr_seq_interno_p bigint, nr_atendimento_p bigint) RETURNS varchar AS $body$
DECLARE


ie_existe_sala_w        varchar(1);
nr_cirurgia_w           bigint;

c01 CURSOR FOR
        SELECT  a.nr_cirurgia
        from    cirurgia a,
                agenda_paciente b
        where   a.nr_cirurgia           = b.nr_cirurgia
        and             a.nr_atendimento        = nr_atendimento_p
        and             coalesce(a.dt_termino::text, '') = ''
		and		a.ie_status_cirurgia not in (3,4)
        order by coalesce(a.dt_inicio_real,a.dt_inicio_prevista) desc;


BEGIN

open C01;
loop
fetch C01 into
        nr_cirurgia_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
end loop;
close C01;


select  coalesce(max('S'),'N')
into STRICT    ie_existe_sala_w
from    agenda_paciente a,
                sala_cirurgia b
where   a.nr_seq_sala_cir       = b.nr_sequencia
and             a.nr_atendimento        = nr_atendimento_p
and             b.nr_seq_interno        = nr_seq_interno_p
and             a.nr_cirurgia           = nr_cirurgia_w;


return  ie_existe_sala_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
