SET client_encoding TO 'UTF8';





CREATE OR REPLACE FUNCTION check_active_ingred ( qt_dose_p bigint default null, cd_material_p bigint DEFAULT NULL, nr_active_ingredient_p bigint DEFAULT NULL, ie_type_verification_p text default null) RETURNS varchar AS $body$
DECLARE


ds_retorno_w				varchar(255);
nr_seq_ficha_tecnica_w			medic_conc_ingrediente.nr_ficha_tecnica%type;
ie_has_multiple_composition_w		smallint;
total_medic_dose_w			bigint;
medic_dose_w				bigint;
corresponding_percentage_w		bigint;
real_prescribed_value_w			bigint;


BEGIN
if (ie_type_verification_p <> 'CV') then
	
	select	max(nr_seq_ficha_tecnica)
	into STRICT	nr_seq_ficha_tecnica_w
	from	material a
	where	a.cd_material =	cd_material_p;
	
	if (nr_seq_ficha_tecnica_w	= nr_active_ingredient_p) then
		return 'S';
	else
		return obter_se_contido(nr_active_ingredient_p,obter_derivados_ficha_medica(nr_seq_ficha_tecnica_w));
	end if;	
end if;

if (ie_type_verification_p = 'CV') then
	select count(*)
	into STRICT	ie_has_multiple_composition_w
	from	medic_conc_ingrediente
	where	cd_material = cd_material_p;
	
	if (ie_has_multiple_composition_w >= 2) then
		select	sum(qt_dose)
		into STRICT	total_medic_dose_w
		from	medic_conc_ingrediente
		where	cd_material = cd_material_p;
	end if;
	
	if (total_medic_dose_w IS NOT NULL AND total_medic_dose_w::text <> '') then
		select	qt_dose
		into STRICT	medic_dose_w
		from	medic_conc_ingrediente
		where	cd_material = cd_material_p
		and	nr_ficha_tecnica = nr_active_ingredient_p;
		
		corresponding_percentage_w := medic_dose_w / total_medic_dose_w * 100;
	end if;
	
	if (qt_dose_p IS NOT NULL AND qt_dose_p::text <> '' AND corresponding_percentage_w IS NOT NULL AND corresponding_percentage_w::text <> '') then
		real_prescribed_value_w := qt_dose_p / 100 * corresponding_percentage_w;
		ds_retorno_w := real_prescribed_value_w;
	end if;
	if ((qt_dose_p IS NOT NULL AND qt_dose_p::text <> '') and coalesce(corresponding_percentage_w::text, '') = '') then
		ds_retorno_w := qt_dose_p;
	end if;

end if;

return	ds_retorno_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
 STABLE;
